String grade;

if (score >= 85) {
grade = “HD”;
} else if (score >= 75) {
grade = “D”;
} else if (score >= 65) {
grade = “C”;
} else if (score >= 50) {
grade = “P”;
} else {
grade = “F”;
}

return grade;
}

public static String computeRGrade(double score) {
String grade;

if (score >= 50) {
grade = “P”;
} else {
grade = “F”;
}

return grade;
}
