export default class GameElement{

    static ARC_WIDTH = 96; 
    constructor(x,y,deltaX =0,deltaY = 0,srcImage){
        this.x = x;
        this.y = y;
        this.deltaX = deltaX;
        this.deltaY = deltaY;
        this.image = this.#createImage(srcImage);
    }

    #createImage(imageSource) {
        const newImg = new Image();
        newImg.src = imageSource;
        return newImg;
    }
    get width() {
      return this.image.width;
    }
    get height() {
      return this.image.height;
    }

    draw(context){
        context.drawImage(this.image,this.x,this.y);
    }

    move(canvas){
        if((this.x + this.deltaX + GameElement.ARC_WIDTH)> canvas.width){
            this.deltaX = -this.deltaX;
        }
        if((this.x + this.deltaX ) < 0){
            this.deltaX = -this.deltaX;
        }
        if((this.y + this.deltaY + GameElement.ARC_WIDTH)> canvas.height){
            this.deltaY = -this.deltaY;
        }
        if((this.y + this.deltaY ) < 0){
            this.deltaY = -this.deltaY;
        }
        this.x = this.x + this.deltaX;
        this.y = this.y +this.deltaY;
    }
}
