void Renderer::CreateDevice() {
    QueueFamilyIndices indices = GetQueueFamilyIndices(physicalDevice);
    std::set<uint32_t> uniqueQueueFamilyIndices = { indices.graphicsFamily.value(), indices.presentFamily.value() };
    float queuePriority = 1.0f;
    std::vector<vk::DeviceQueueCreateInfo> queueCreateInfos;
    for (uint32_t queueFamilyIndex : uniqueQueueFamilyIndices) {
        queueCreateInfos.emplace_back(vk::DeviceQueueCreateInfo{{}, queueFamilyIndex, 1, &queuePriority});
    }
    vk::PhysicalDeviceFeatures deviceFeatures;
    deviceFeatures.samplerAnisotropy = VK_TRUE;
    vk::DeviceCreateInfo createInfo{{}, static_cast<uint32_t>(queueCreateInfos.size()), queueCreateInfos.data(), 0, nullptr, static_cast<uint32_t>(deviceExtensions.size()), deviceExtensions.data(), &deviceFeatures};
    device = physicalDevice.createDevice(createInfo);
    graphicsQueue = device.getQueue(indices.graphicsFamily.value(), 0);
    presentQueue = device.getQueue(indices.presentFamily.value(), 0);
}

void Renderer::CleanupDevice() {
    pipeline.reset();
    if (device) {
        device.destroy();
        device = nullptr;
    }
}

