(* Define the differential equation and boundary conditions *)
(* Example: Solve y''(x) + y(x) = 0, y(0) = 0, y(π/2) = 1 *)
ClearAll[y, x];
de = y''[x] + y[x] == 0;
bcs = {y[0] == 0, y[Pi/2] == 1};

(* 1. Ritz Method *)
(* Use a trial function yRitz[x_] = a x (π/2 - x) + b x, minimize the residual *)
yRitz[x_] := a x (Pi/2 - x) + b x;
residual = (D[yRitz[x], {x, 2}] + yRitz[x])^2;
I = Integrate[residual, {x, 0, Pi/2}];
solRitz = Solve[{D[I, a] == 0, D[I, b] == 0, yRitz[0] == 0, yRitz[Pi/2] == 1}, {a, b}];
yRitzSol[x_] = yRitz[x] /. solRitz[[1]];

(* 2. Method of Concatenated Elements (Finite Element Method) *)
(* Discretize the domain and solve numerically *)
nodes = 5;
mesh = Table[xi, {xi, 0, Pi/2, (Pi/2)/(nodes - 1)}];
feSol = NDSolve[{de, Sequence @@ bcs}, y, {x, 0, Pi/2},
  Method -> {"FiniteElement", "MeshOptions" -> {"MaxCellMeasure" -> 0.1}}];
yFE[x_] := y[x] /. feSol[[1]];

(* 3. Method of Conjugate Distinctions (Conjugate Gradient for boundary value problems) *)
(* Discretize and solve using iterative method *)
mat = NDSolveValue[{de, Sequence @@ bcs}, y, {x, 0, Pi/2},
  Method -> {"BoundaryValueProblem", "LinearSolveMethod" -> "ConjugateGradient"}];
yConj[x_] := mat[x];

(* 4. Standard Mathematica Library *)
solStandard = NDSolve[{de, Sequence @@ bcs}, y, {x, 0, Pi/2}];
yStandard[x_] := y[x] /. solStandard[[1]];

(* Display results and plot comparison *)
Print["Ritz method solution at x=1: ", yRitzSol[1]];
Print["Finite Element solution at x=1: ", yFE[1]];
Print["Conjugate Gradient solution at x=1: ", yConj[1]];
Print["Standard NDSolve solution at x=1: ", yStandard[1]];

Plot[{yRitzSol[x], yFE[x], yConj[x], yStandard[x]}, {x, 0, Pi/2},
 PlotLegends -> {"Ritz", "Finite Element", "Conjugate Gradient", "Standard"},
