import os
import cv2
import random

def preprocess_image(image):
    # Example preprocessing: convert to grayscale and resize to fixed height
    image = cv2.cvtColor(image, cv2.COLOR_BGR2GRAY)
    image = cv2.resize(image, (image.shape[1], 100))
    return image

def get_image_pairs(dataset_path):
    subfolders = [f for f in os.listdir(dataset_path) if os.path.isdir(os.path.join(dataset_path, f))]
    subfolder_images = {}
    width_threshold = 600
    # Step 1: Preprocess and categorize images
    for subfolder in subfolders:
        folder_path = os.path.join(dataset_path, subfolder)
        images = []
        for img_name in os.listdir(folder_path):
            img_path = os.path.join(folder_path, img_name)
            img = cv2.imread(img_path)
            if img is None:
                continue
            processed = preprocess_image(img)
            width = processed.shape[1]
            images.append({'name': img_name, 'path': img_path, 'width': width, 'data': processed})
        small = [img for img in images if img['width'] < width_threshold]
        large = [img for img in images if img['width'] >= width_threshold]
        subfolder_images[subfolder] = {'small': small, 'large': large}
    # Step 2: Form positive pairs
    positive_pairs = []
    for subfolder, groups in subfolder_images.items():
        for size in ['small', 'large']:
            imgs = groups[size]
            for i in range(len(imgs)):
                for j in range(i+1, len(imgs)):
                    positive_pairs.append(((subfolder, imgs[i]['name']), (subfolder, imgs[j]['name'])))
    # Step 3: Form negative pairs
    negative_pairs = []
    subfolder_list = list(subfolder_images.keys())
    for idx, subfolder in enumerate(subfolder_list):
        for size in ['small', 'large']:
            imgs = subfolder_images[subfolder][size]
            # Collect all images from other subfolders in the same size category
            other_imgs = []
            for other_idx, other_subfolder in enumerate(subfolder_list):
                if other_subfolder == subfolder:
                    continue
                other_imgs.extend([(other_subfolder, img['name']) for img in subfolder_images[other_subfolder][size]])
            for img in imgs:
                if other_imgs:
                    neg_pair = random.choice(other_imgs)
                    negative_pairs.append(((subfolder, img['name']), neg_pair))
    return positive_pairs, negative_pairs

# Usage:
# pos, neg = get_image_pairs('/path/to/dataset')
# print('Positive pairs:', pos)
# print('Negative pairs:', neg)

