// Converts IPv4 string to uint32
func IPv4ToLong(ipStr string) (uint32, error) {
	parts := strings.Split(ipStr, ".")
	if len(parts) != 4 {
		return 0, errors.New("invalid IPv4 address")
	}
	var ip uint32
	for i := 0; i < 4; i++ {
