		if err != nil || p < 0 || p > 255 {
			return 0, errors.New("invalid IPv4 segment")
		}
		ip |= uint32(p) << (8 * uint(i))
	}
	return ip, nil
}

// Converts uint32 to IPv4 string
func LongToIPv4(ip uint32) string {
	return fmt.Sprintf("%d.%d.%d.%d", ip&0xFF, (ip>>8)&0xFF, (ip>>16)&0xFF, (ip>>24)&0xFF)
}

// Converts IPv6 string to custom byte string
func IPv6ToStrByte(ipv6 string) (string, error) {
	ip := net.ParseIP(ipv6)
	if ip == nil || ip.To16() == nil {
		return "", errors.New("invalid IPv6 address")
	}
	ip = ip.To16()
	var parts []string
	for i := 0; i < 16; i += 4 {
		val := uint64(ip[i])<<24 | uint64(ip[i+1])<<16 | uint64(ip[i+2])<<8 | uint64(ip[i+3])
		parts = append(parts, strconv.FormatUint(val, 10))
	}
	return strings.Join(parts, "-"), nil
}

// Converts custom byte string to IPv6 string
func StrByteToIPv6(strByte string, streamline ...bool) (string, error) {
	parts := strings.Split(strByte, "-")
	if len(parts) != 4 {
		return "", errors.New("invalid strByte for IPv6")
	}
	var b [16]byte
	for i, part := range parts {
		val, err := strconv.ParseUint(part, 10, 32)
		if err != nil {
			return "", errors.New("invalid segment in strByte")
		}
		b[i*4] = byte(val >> 24)
		b[i*4+1] = byte((val >> 16) & 0xFF)
		b[i*4+2] = byte((val >> 8) & 0xFF)
		b[i*4+3] = byte(val & 0xFF)
	}
	ip := net.IP(b[:]).String()
	if len(streamline) > 0 && streamline[0] {
		return ip, nil // net.IP.String() already compresses
	}
	// Expand all zeros (no compression)
	return strings.ReplaceAll(ip, "::", strings.Repeat(":0", 8-len(parts)+1)), nil
}

// Converts IP (v4 or v6) to custom byte string
func IPToStrByte(ip string) (string, error) {
	if strings.Contains(ip, ":") {
		return IPv6ToStrByte(ip)
	}
	if strings.Contains(ip, ".") {
		val, err := IPv4ToLong(ip)
		if err != nil {
			return "", err
		}
		return strconv.FormatUint(uint64(val), 10), nil
	}
	return "", errors.New("invalid IP address")
}

// Converts custom byte string to IP (v4 or v6)
func StrByteToIP(strByte string) (string, error) {
	if strings.Contains(strByte, "-") {
		return StrByteToIPv6(strByte)
	}
	val, err := strconv.ParseUint(strByte, 10, 32)
	if err != nil {
		return "", errors.New("invalid strByte for IPv4")
	}
	return LongToIPv4(uint32(val)), nil
}

