import Quiver from './quiver.js';

export default class Game {
    // ... existing code ...
    constructor(canvas) {
        // ... existing code ...
        this.quiver = null;
        this.lastQuiverTime = 0;
        this.quiverInterval = 1500; // 1.5 seconds
    }

    animate(timestamp) {
        this.context.clearRect(0,0,this.#canvas.width,this.#canvas.height);
        this.arc.handleMoveKeys(this.keyManager);
        this.arc.move(this.canvas);
        this.arc.draw(this.context);
        this.target.draw(this.context);
        if(this.hasShoot){
            this.arrow.move();
            this.arrow.draw(this.context);
        }
        if(this.nbArrow<0){
            this.hasShoot=false;
        }
        this.afficheNbArrow.textContent = this.nbArrow;

        // Quiver appearance logic
        if (!this.lastQuiverTime || timestamp - this.lastQuiverTime > this.quiverInterval) {
            this.lastQuiverTime = timestamp;
            if (Math.random() < 0.5) {
                const y = 100 + Math.floor(Math.random() * 301); // 100 to 400 px
                const x = Math.floor(Math.random() * (this.#canvas.width - Quiver.QUIVER_WIDTH));
                this.quiver = new Quiver(x, y);
            }
        }
        // Draw quiver if present
        if (this.quiver) {
            this.quiver.draw(this.context);
        }

        // Collision with arc (bow)
        if (this.quiver && this.collision(this.arc, this.quiver)) {
            this.nbArrow = 5;
            this.quiver = null;
        }
        // Collision with bird (assuming birds array exists)
        if (this.quiver && this.birds) {
            for (let bird of this.birds) {
                if (this.collision(bird, this.quiver)) {
                    this.quiver = null;
                    break;
                }
            }
        }
        this.animation = window.requestAnimationFrame(this.animate.bind(this));
    }

    // Simple AABB collision detection
    collision(a, b) {
        return (
            a.x < b.x + b.width &&
            a.x + a.width > b.x &&
            a.y < b.y + b.height &&
            a.y + a.height > b.y
        );
    }
    // ... rest of the code ...
}

