//| Expert Advisor to move Stop Loss to Entry at Take Profit 2      |
//+------------------------------------------------------------------+
input double TakeProfit2 = 100; // Take Profit 2 in points
input int Slippage = 3;         // Slippage in points

// Function to move SL to entry when TP2 is reached
void MoveSLToEntryAtTP2() {
   for(int i=OrdersTotal()-1; i>=0; i--) {
      if(OrderSelect(i, SELECT_BY_POS, MODE_TRADES)) {
         if(OrderSymbol() == Symbol() && OrderType() == OP_BUY) {
            double entry = OrderOpenPrice();
            double tp2 = entry + TakeProfit2 * Point;
            if(Bid >= tp2 && OrderStopLoss() < entry) {
               bool res = OrderModify(OrderTicket(), entry, entry, OrderTakeProfit(), 0, clrGreen);
               if(res) Print("Stop Loss moved to entry at TP2 for BUY order.");
               else Print("Failed to move SL to entry: ", GetLastError());
            }
         }
         if(OrderSymbol() == Symbol() && OrderType() == OP_SELL) {
            double entry = OrderOpenPrice();
            double tp2 = entry - TakeProfit2 * Point;
            if(Ask <= tp2 && (OrderStopLoss() > entry || OrderStopLoss() == 0)) {
               bool res = OrderModify(OrderTicket(), entry, entry, OrderTakeProfit(), 0, clrRed);
               if(res) Print("Stop Loss moved to entry at TP2 for SELL order.");
               else Print("Failed to move SL to entry: ", GetLastError());
            }
         }
      }
   }
}

// Call this function in OnTick()
void OnTick() {
   MoveSLToEntryAtTP2();
}

