#!/usr/bin/env python3
import os
import re
import sys

def find_weapons_meta_files(base_folder):
    weapons_meta_files = []
    for root, dirs, files in os.walk(base_folder):
        for file in files:
            if file == 'weapons.meta':
                weapons_meta_files.append(os.path.join(root, file))
    return weapons_meta_files

def extract_weapon_names(file_path):
    weapon_names = set()
    try:
        with open(file_path, 'r', encoding='utf-8') as f:
            content = f.read()
            # Find all occurrences of <Name>WEAPON_...</Name>
            matches = re.findall(r'<Name>(WEAPON_[A-Z0-9_]+)</Name>', content)
            weapon_names.update(matches)
    except Exception as e:
        print(f"Error reading {file_path}: {e}")
    return weapon_names

def main(base_folder, output_txt):
    all_weapon_names = set()
    weapons_meta_files = find_weapons_meta_files(base_folder)
    if not weapons_meta_files:
        print("No weapons.meta files found.")
        return
    for meta_file in weapons_meta_files:
        weapon_names = extract_weapon_names(meta_file)
        if weapon_names:
            all_weapon_names.update(weapon_names)
    if not all_weapon_names:
        print("No weapon names found in any weapons.meta file.")
        return
    try:
        with open(output_txt, 'a', encoding='utf-8') as out_f:
            for name in sorted(all_weapon_names):
                out_f.write(name + '\n')
        print(f"Appended {len(all_weapon_names)} weapon names to {output_txt}.")
    except Exception as e:
        print(f"Error writing to {output_txt}: {e}")

if __name__ == '__main__':
    if len(sys.argv) != 3:
        print("Usage: python script.py <base_folder> <output_txt>")
        sys.exit(1)
    main(sys.argv[1], sys.argv[2])

